import React, { Component } from 'react';
import ProgressBar from 'react-bootstrap/ProgressBar'
import axios from 'axios';


export default class Home extends Component {
  constructor(props){
    super(props)
    this.state={
      exerciseProgress: 0,
      exerciseGoal: 0,
      sleepProgress:0,
      sleepGoal:0,
      waterProgress: 0,
      waterGoal: 0,
    }
  }

  componentDidMount(){
    axios.get('http://localhost:5000/exercise_track/getOne')
    .then(response =>{
      if(response.data.length >= 1){
        console.log(response.data)
        this.setState({
          exerciseProgress: response.data[0]['hours']
        });
      }
    })

    axios.get('http://localhost:5000/exercise_goal/')
    .then(response =>{
      if(response.data.length >= 1){
        this.setState({
          exerciseGoal: response.data[0]['hours']
        });
      }
    })

    axios.get('http://localhost:5000/sleep_track/getOne')
    .then(response =>{
      if(response.data.length >= 1){
        console.log(response.data)
        this.setState({
        sleepProgress: response.data[0]['hours']
        });
      }
    })

    axios.get('http://localhost:5000/sleep_goal/')
    .then(response =>{
      if(response.data.length >= 1){
        this.setState({
          sleepGoal: response.data[0]['hours']
        });
      }
    })

    axios.get('http://localhost:5000/water_track/getOne')
    .then(response =>{
      if(response.data.length >= 1){
        console.log(response.data)
        this.setState({
          waterProgress: response.data[0]['cups']
        });
      }
    })

    axios.get('http://localhost:5000/water_goal/')
    .then(response =>{
      if(response.data.length >= 1){
        this.setState({
          waterGoal: response.data[0]['cups']
        });
      }
    })

    if (this.state.waterGoal / 2 > this.state.waterProgress){
      console.log("hello")
      alert("Please drink more water to prevent deyhdration")
    }

  }

  render() {
    var exercisePercentage = Math.round(this.state.exerciseProgress / this.state.exerciseGoal * 100)
    if (exercisePercentage > 100){
      exercisePercentage = 100
    }

    var sleepPercentage = Math.round(this.state.sleepProgress / this.state.sleepGoal * 100)
    if (sleepPercentage > 100){
      sleepPercentage = 100
    }

    var waterPercentage = Math.round(this.state.waterProgress / this.state.waterGoal * 100)
    if (waterPercentage > 100){
      waterPercentage = 100
    }
    return (
      <div>
        <h3> HomePage </h3>
        <br/>
        <text> Exercise Goal Progress </text>
        <ProgressBar now={exercisePercentage} label={`${exercisePercentage}%`}/>
        <text> Sleep Goal Progress </text>
        <ProgressBar now={sleepPercentage} label={`${sleepPercentage}%`}/>
        <text> Water Goal Progress </text>
        <ProgressBar now={waterPercentage} label={`${waterPercentage}%`}/>
        <br/>
        <b> Health Score :  {exercisePercentage + sleepPercentage + waterPercentage} </b>

      </div>
    )
  }
}
