import React, { Component } from 'react';
import ProgressBar from 'react-bootstrap/ProgressBar'
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import 'react-vis/dist/style.css';
import {XYPlot, XAxis, YAxis, HorizontalGridLines, LineSeries, DiscreteColorLegend} from 'react-vis';


export default class Home extends Component {
  constructor(props){
    super(props)
    this.state={
      exerciseProgress: 0,
      exerciseGoal: 0,
      sleepProgress:0,
      sleepGoal:0,
      waterProgress: 0,
      waterGoal: 0,
    }
  }

  componentDidMount(){
    axios.get('http://localhost:5000/exercise_track/getOne')
    .then(response =>{
      if(response.data.length >= 1){
        this.setState({
          exerciseProgress: response.data[0]['hours']
        });
      }
    })

    axios.get('http://localhost:5000/exercise_goal/')
    .then(response =>{
      if(response.data.length >= 1){
        this.setState({
          exerciseGoal: response.data[0]['hours']
        });
      }
    })
  }

  render() {
    return (
      <div>
        <h3> HomePage </h3>
        <ProgressBar percentage={this.state.exerciseProgress / this.state.exerciseGoal}/>
      </div>
    )
  }
}
