import React, { Component } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import 'react-vis/dist/style.css';
import {XYPlot, XAxis, YAxis, HorizontalGridLines, LineSeries} from 'react-vis';

const Exercise = props => (
  <tr>
    <td>{props.exercise.hours}</td>
    <td>{props.exercise.date}</td>
  </tr>
)

var test = [{x: new Date('01/01/2018'), y:10}, {x: new Date('01/02/2018'), y:12}]


export default class ExerciseTrack extends Component {

  constructor(props) {
    super(props);
    this.state = {
      exercises: [],
      goals: [],
      ideal: [],
      hours: 0,
      hoursGoal: 0,
      date: new Date(),
      dateGoal: new Date(),
    }

    this.onChangeHours = this.onChangeHours.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.onChangeHoursGoal = this.onChangeHoursGoal.bind(this);
    this.onChangeDateGoal = this.onChangeDateGoal.bind(this);
    this.onSubmitGoal = this.onSubmitGoal.bind(this);

    this.exerciseList = this.exerciseList.bind(this);
    this.goalList = this.goalList.bind(this);
  }

  componentDidMount() {
    var e = []
    var f = []
    axios.get('http://localhost:5000/exercise_track/')
     .then(response => {
       console.log(response.data)
        var newArray = []
        var uniqueObject = {};
        for (var i = response.data.length - 1; i >= 0; i--){
          var date = response.data[i]['date']
          uniqueObject[date] = response.data[i]
        }

        for (i in uniqueObject) {
          newArray.push(uniqueObject[i]);
        }

        console.log(newArray)


        e = newArray.map((exercise)=>{
          return {x: new Date(exercise.date), y: exercise.hours}
        });

        this.setState({
          exercises: e
        });
        f = response.data.map((exercise)=>{
          return {x: new Date(exercise.date), y: 21}
        });
        this.setState({
          ideal: f
        });
      })
     .catch((error) => {
        console.log(error);
     })

     var e = []
     axios.get('http://localhost:5000/exercise_goal/')
      .then(response => {
        console.log(response.data)
         var newArray = []
         var uniqueObject = {};
         for (var i = response.data.length - 1; i >= 0; i--){
           var date = response.data[i]['date']
           uniqueObject[date] = response.data[i]
         }

         for (i in uniqueObject) {
           newArray.push(uniqueObject[i]);
         }

         e = newArray.map((exercise)=>{
           return {x: new Date(exercise.date), y: exercise.hours}
         });

         this.setState({
           goals: e
         });
       })
      .catch((error) => {
         console.log(error);
      })
  }

  exerciseList() {
    return this.state.exercises.map(currentexercise => {
      return <Exercise exercise={currentexercise} key={currentexercise._id}/>;
    })
  }

  onChangeHours(e) {
    this.setState({
      hours: e.target.value
    });
  }

  onChangeDate(date) {
    this.setState({
      date: date
    });
  }

  onSubmit(e) {
    e.preventDefault();
    const exercise = {
      hours: this.state.hours,
      date: this.state.date,
    };
    console.log(exercise);
    axios.post('http://localhost:5000/exercise_track/add', exercise).then(res => console.log(res.data));

  }




  goalList() {
    return this.state.goals.map(currentexercise => {
      return <Exercise exercise={currentexercise} key={currentexercise._id}/>;
    })
  }

  onChangeHoursGoal(e) {
    this.setState({
      hoursGoal: e.target.value
    });
  }

  onChangeDateGoal(date) {
    this.setState({
      dateGoal: date
    });
  }

  onSubmitGoal(e) {
    e.preventDefault();
    const exercise = {
      hours: this.state.hours,
      date: this.state.date,
    };
    console.log(exercise);
    axios.post('http://localhost:5000/exercise_goal/add', exercise).then(res => console.log(res.data));

  }

  render() {
    console.log(this.state.exercises)

    return (
      <div>
        <h3>Create New Exercise Log</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Minutes: </label>
            <input
                type="text"
                className="form-control"
                value={this.state.hours}
                onChange={this.onChangeHours}
                />
          </div>
          <div className="form-group">
            <label>Date: </label>
            <div>
              <DatePicker
                selected={this.state.date}
                onChange={this.onChangeDate}
              />
            </div>
          </div>
          <div className="form-group">
            <input type="submit" value="Create Exercise Log" className="btn btn-primary" />
          </div>
        </form>
        <h3>Set New Exercise Goal</h3>
        <form onSubmit={this.onSubmitGoal}>
          <div className="form-group">
            <label>Minutes: </label>
            <input
                type="text"
                className="form-control"
                value={this.state.hoursGoal}
                onChange={this.onChangeHoursGoal}
                />
          </div>
          <div className="form-group">
            <label>Date: </label>
            <div>
              <DatePicker
                selected={this.state.dateGoal}
                onChange={this.onChangeDateGoal}
              />
            </div>
          </div>
          <div className="form-group">
            <input type="submit" value="Set Exercise Goal" className="btn btn-primary" />
          </div>
        </form>
        <h3>View Exercise History</h3>
        <XYPlot xType="time" height={300} width= {300}>
          <HorizontalGridLines />
          <XAxis title="Date" />
          <YAxis title="Minutes Exercised"/>
            <LineSeries data={this.state.exercises} />
            <LineSeries data={this.state.ideal} />
            <LineSeries data={this.state.goals} />
        </XYPlot>
      </div>
    )
  }
}
