import React, { Component } from 'react';
import axios from 'axios';

const Exercise = props => (
  <tr>
    <td>{props.exercise.hours}</td>
  </tr>
)

export default class ExerciseGoal extends Component {

  constructor(props) {
    super(props);
    this.state = {
      hours: 0
    }

    this.onChangeHours = this.onChangeHours.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.exerciseList = this.exerciseList.bind(this);
  }

  componentDidMount() {
    var e = []
    var f = []
    axios.get('http://localhost:5000/exercise_track/')
     .then(response => {
       console.log(response.data)
        var newArray = []
        var uniqueObject = {};
        for (var i = response.data.length - 1; i >= 0; i--){
          var date = response.data[i]['date']
          uniqueObject[date] = response.data[i]
        }

        for (i in uniqueObject) {
          newArray.push(uniqueObject[i]);
        }

        console.log(newArray)


        e = newArray.map((exercise)=>{
          return {x: new Date(exercise.date), y: exercise.hours}
        });

        this.setState({
          exercises: e
        });
        f = response.data.map((exercise)=>{
          return {x: new Date(exercise.date), y: 21}
        });
        this.setState({
          ideal: f
        });
      })
     .catch((error) => {
        console.log(error);
     })
  }

  exerciseList() {
    return this.state.exercises.map(currentexercise => {
      return <Exercise exercise={currentexercise} key={currentexercise._id}/>;
    })
  }

  onChangeHours(e) {
    this.setState({
      hours: e.target.value
    });
  }

  onChangeDate(date) {
    this.setState({
      date: date
    });
  }

  onSubmit(e) {
    e.preventDefault();
    const exercise = {
      hours: this.state.hours,
      date: this.state.date,
    };
    console.log(exercise);
    axios.post('http://localhost:5000/exercise_track/add', exercise).then(res => console.log(res.data));

  }

  render() {
    console.log(this.state.exercises)

    return (
      <div>
        <h3>Create New Exercise Log</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Minutes: </label>
            <input
                type="text"
                className="form-control"
                value={this.state.hours}
                onChange={this.onChangeHours}
                />
          </div>
          <div className="form-group">
            <label>Date: </label>
            <div>
              <DatePicker
                selected={this.state.date}
                onChange={this.onChangeDate}
              />
            </div>
          </div>
          <div className="form-group">
            <input type="submit" value="Create Exercise Log" className="btn btn-primary" />
          </div>
        </form>
        <h3>View Exercise History</h3>
        <XYPlot xType="time" height={300} width= {300}>
          <HorizontalGridLines />
          <XAxis title="Date" />
          <YAxis title="Minutes Exercised"/>
            <LineSeries data={this.state.exercises} />
            <LineSeries data={this.state.ideal} />
        </XYPlot>
      </div>
    )
  }
}
