import React, { Component } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import 'react-vis/dist/style.css';
import {XYPlot, XAxis, YAxis, HorizontalGridLines, LineSeries} from 'react-vis';

const Exercise = props => (
  <tr>
    <td>{props.exercise.hours}</td>
    <td>{props.exercise.date}</td>
  </tr>
)

var exercises = []

export default class ExerciseTrack extends Component {

  constructor(props) {
    super(props);
    this.state = {
      test: [{x:1, y:2}, {x:2, y:4}],
      hours: 0,
      date: new Date()
    }

    this.onChangeHours = this.onChangeHours.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.exerciseList = this.exerciseList.bind(this);
  }

  componentDidMount() {
    axios.get('http://localhost:5000/exercise_track/')
     .then(response => {
        exercises = response.data
        var objs = JSON.parse(exercises);
        for (var i = 0; i < objs.length(); i++){
          var obj = objs[i]
          obj.x = obj.date
          obj.y = obj.hours
          delete obj.date
          delete obj.hours

        }
        console.log(exercises);

      })
     .catch((error) => {
        console.log(error);
     })
  }

  exerciseList() {
    return exercises.map(currentexercise => {
      return <Exercise exercise={currentexercise} key={currentexercise._id}/>;
    })
  }

  onChangeHours(e) {
    this.setState({
      hours: e.target.value
    });
  }

  onChangeDate(date) {
    this.setState({
      date: date
    });
  }

  onSubmit(e) {
    e.preventDefault();
    const exercise = {
      hours: this.state.hours,
      date: this.state.date,
    };
    console.log(exercise);
    axios.post('http://localhost:5000/exercise_track/add', exercise).then(res => console.log(res.data));
  }

  render() {
    return (
      <div>
        <h3>Create New Exercise Log</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Hours: </label>
            <input
                type="text"
                className="form-control"
                value={this.state.hours}
                onChange={this.onChangeHours}
                />
          </div>
          <div className="form-group">
            <label>Date: </label>
            <div>
              <DatePicker
                selected={this.state.date}
                onChange={this.onChangeDate}
              />
            </div>
          </div>
          <div className="form-group">
            <input type="submit" value="Create Exercise Log" className="btn btn-primary" />
          </div>
        </form>
        <XYPlot xType="time" height={300} width= {300}>
          <HorizontalGridLines />
          <LineSeries data={this.state.test} />
          <XAxis title="Date" />
          <YAxis title="Hours Exercised"/>
        </XYPlot>
      </div>
    )
  }
}

